package com.hisrv.lib.anetier;import java.util.Locale;public class LanguageUtils {	public static boolean isEnglish() {		Locale local = Locale.getDefault();		String language = String.format("%s-%s", local.getLanguage(),				local.getCountry());		if ("en-US".equals(language)) {			return true;		} else {			return false;		}	}	public static boolean isCommonEnglish() {		Locale local = Locale.getDefault();		String language = local.getLanguage();		if ("en".equals(language)) {			return true;		} else {			return false;		}	}	/**	 * Whether the language is simplified Chinese	 * 	 * @return	 */	public static boolean isSimplifiedChinese() {		Locale local = Locale.getDefault();		String language = String.format("%s-%s", local.getLanguage(),				local.getCountry());		if ("zh-CN".equals(language)) {			return true;		} else {			return false;		}	}	/**	 * Whether the language is traditional Chinese	 * 	 * @return	 */	public static boolean isTraditionalChinese() {		Locale local = Locale.getDefault();		String language = String.format("%s-%s", local.getLanguage(),				local.getCountry());		if (null == language) {			return false;		} else if ("zh-CN".equals(language)) {			return false;		} else if (language.startsWith("zh")) {			return true;		} else {			return false;		}	}	/**	 * Whether the language is Chinese including both simplified and traditional	 * 	 * @return	 */	public static boolean isChinese() {		return !isForeignLanguage();	}	public static boolean isForeignLanguage() {		Locale local = Locale.getDefault();		String language = String.format("%s", local.getLanguage());		if (!"zh".equals(language)) {			return true;		} else {			return false;		}	}	public static boolean isKorean() {		Locale local = Locale.getDefault();		String language = String.format("%s-%s", local.getLanguage(),				local.getCountry());		if ("ko-KR".equals(language)) {			return true;		} else {			return false;		}	}	public static String getLanguage() {		Locale local = Locale.getDefault();		if (local != null) {			String lang = local.getLanguage();			String country = local.getCountry();			if (lang != null && lang.equalsIgnoreCase("zh")) {				if (country != null && country.equalsIgnoreCase("cn")) {					return lang + "-" + "CN";				} else {					return lang + "-" + "TW";				}			}			return lang;		}		return "zh";	}}